    public void armazena(View view) {

        EditText salvarEditText = (EditText) findViewById(R.id.guardaEditText);
        String dados = salvarEditText.getText().toString();

        Bundle guarda = new Bundle();
        guarda.putString("campo", dados);

        Toast.makeText(getApplicationContext(), dados, Toast.LENGTH_SHORT).show();

        Log.i("LogCat", dados);


        Intent intent = new Intent(this, SegundaActivity.class);
        intent.putExtras(guarda);

        startActivity(intent);


    }


